/* Кинопоиск - белый фон v2.0 (10.07.12)   */
/* Автор - PoZitron                        */
/* Делает фон Кинопоиска белым             */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kinopoisk.ru")
{
/* делаю фон белым */
html {background-color: #FFFFFF !important}
html {background: none !important}
body {background: none !important}

/* убираю некрасивую тень */
.shadow {box-shadow: none !important}

/* убираю пустоту вверху (поднимаем "шапку") */
.menu {top:40px !important}
#top_form {top:40px !important}
.png_block {top:40px !important}

/* подгоняю под "шапку" */
#top {height: 120px !important}

/* убираю баннер загораживающие ссылки */
#branding_flash {display: none !important}
.search_line_image {display: none !important}

/* форма поиска всегда будет чёрной */
.png_block {background-color: #000000 !important}

}
