css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://anon.fi/") {
body, html { background:#0A0A0A !important; margin:0 !important; padding:0 !important; }

body,input,textarea,blockquote,p,span[style="float: right;"] {
color:cyan !important;
font-family:OCR A Std  !important;
font-size:10pt !important;
}

h1 { color:#33FFFF  !important; font-size:3em !important; }
h2 { color:#000  !important; font-size:3em !important; background:#33FFFF !important; }
HR { color:#33FFFF !important; }

a { color:cyan !important; font-family:OCR A Std  !important; }
a:hover { color:cyan !important; text-decoration:line-through !important; }

a img { border:0px solid cyan  !important; }
a img:hover { border:0px solid cyan !important; }
select { display:none !important; }

a img[src^="http://www.kuvalauta.fi/css/icons/"] { border:0 !important; }
a:hover img[src^="http://www.kuvalauta.fi/css/icons/"] { border:0 !important; }


.navbar, .adminbar { background:black !important; color:; margin-bottom:1em; padding:1em; }
.huge_main { color:#000  !important; font-size:3em !important; background:#33FFFF !important; }
.title { color:#33FFFF  !important; }

div[style="font-size: 12px; text-align: center;"] { padding-bottom:1em !important; }

div.logo { color:cyan !important; font-size:3em !important; }

.replymode { background:#111 !important; color:#33FFFF !important; width:auto !important; margin:0.5em 0 !important; padding:0.5em !important; }

table.postform td.postblock { color:cyan !important; background:black !important; border-style:none !important; }

.reply { background:black !important; border-color:#33FFFF !important; padding:1em !important; }
.reply label { color:cyan !important; }
.filesize { color:cyan !important; }

.highlight { background:#000 !important; border-color:cyan !important; padding:1em !important; }
.highlight label { color:cyan !important; }
.postername { color:cyan !important; }
.postertrip { color:cyan !important; }
.logo  { font-family:OCR A Std  !important; }
.navbar { font-family:OCR A Std  !important; }
LI { display:none  !important; }
.unkfunc { font-family:OCR A Std !important;  color:lime  !important; }
.filetitle { color:red !important; }
.random { background:#000 !important; border:2px solid cyan !important }
.ban { background:#000 !important; border:2px solid cyan !important }

.reflinkpreview { background:#000 !important; color:#000 !important; border:5px solid #33FFFF !important; padding:1em !important; }
.reflinkpreview .reply { background:#000 !important; color:#33FFFF !important; border:1px solid #33FFFF !important; }
.doubledash { color:#33FFFF !important; }

.pages { background:#111 !important; color:#33FFFF !important; border-style:none !important; }
.pages td { color:cyan !important; }

.userdelete { color:#33FFFF !important; border-collapse:collapse !important; position:relative !important; top:90px !important; left:0px !important; }
.userdelete td { background:#111 !important; color:cyan !important; padding:0.5em !important; text-align:left !important; }
}

img[src="http://www.kuvalauta.fi/bannerit/mainos.png"]
{
   display: none;
}
