css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.) {
  img[src$="/images/logo.gif"] {
    width: 0 !important;
    height: 0px !important;
    padding-left: 400px !important;
    background-image: url(http://4.bp.blogspot.com/_87FWjfCC0iY/TKdhAFnag7I/AAAAAAAALuU/RjM87vcHKM0/s320/NobleTeam.jpg) !important;
    background-position: center left !important;
    background-repeat: no-repeat !important;
  }

  div[title="Google"] {
    background-image: url(http://4.bp.blogspot.com/_87FWjfCC0iY/TKdhAFnag7I/AAAAAAAALuU/RjM87vcHKM0/s320/NobleTeam.jpg) !important;
    background-position: bottom left !important;
    background-repeat: no-repeat !important;
    width: 320px !important;
    height: 200px !important;
  }

  div[title="Google"] > div {
    left: 210px !important;
    top: 95px !important;
    opacity: 0 !important;
  }
}




@-moz-document url-prefix("http://www.google.com") {

  img[src$="/images/logos/ps_logo2.png"]{
  width: 0px !important;
  background-image: url(http://4.bp.blogspot.com/_87FWjfCC0iY/TKdhAFnag7I/AAAAAAAALuU/RjM87vcHKM0/s320/NobleTeam.jpg)!important;
  background-position: bottom left !important;
  padding-left: 360px !important;
  padding-top: 70px !important;
  background-repeat: no-repeat;

  }
}
