css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("railstutorial.org") {
  
  #header,
  #book_top,
  #book_bottom {
    display: none !important;
  }
  
  body {
    margin: 0 !important;
    background: #fff !important;
    overflow: visible !important;
    overflow-x: visible !important;
    overflow-y: visible !important;
  }

  #container,
  #menu,
  #book,
  #book_menu,
  #book * {
    width: auto !important;
  }

  #container {
    height: auto !important;
    padding: 0 !important;
    margin: 0 !important;
  }

  #menu {
    height: auto !important;
    padding: 0 !important;
    margin: 0 !important;
    color: #bbb !important;
    background: -moz-linear-gradient(#ddd, #fff) !important;
  }
  #menu a {
    color: #666 !important;
  }

  #content {
    width: 960px !important;
    margin: auto !important;
  }
  
  #main{
    margin: 0 230px 0 0 !important;
  }

  #book {
    padding: 0 !important;
    font-size: 18px !important;
    background: #fff !important;
  }

  tt,
  code {
    background: #e6e6e6 !important;
  }
  tt,
  code,
  .highlight pre {
    font-size: 90% !important;
    font-family: monospace !important;
    overflow: auto !important;
  }
  pre {
    font-size: 90% !important;
    font-family: monospace !important;
  }
  .highlight {
    padding: 0 !important;
  }
  .highlight pre {
    padding: 10px 15px !important;
    margin: 0 !important;
  }
  
  #book_menu {
    margin: 0 0 10px !important;
    color: #aaa !important;
    text-align: left !important;
  }
  #book_menu a {
    opacity: .7 !important;
  }
  #book_menu a:hover {
    opacity: 1 !important;
  }

  h1.title {
    padding: 0 !important;
    border: 0 !important;
    font-size: 36px !important;
  }
  h1.subtitle {
    font-size: 25px !important;
  }
  h2.author {
    font-size: 18px !important;
  }
  h1.contents {
    font-size: 25px !important;
  }
  
}
