css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){

.speeddialThumb {
margin:14px !important;
border:0px #fff solid;
-moz-border-radius: 6px !important;
padding:9px;
background:#fff ;
-moz-box-shadow: 2px 3px 4px rgba(0,0,0,0.4);

/* created by treasury */
}

.speeddialThumb:hover{
background:#4083D6 !important;
}
.speeddial-top-middle {background: none !important;position:relative !important; left: 200px !important;}
.speeddial-top-middle, .speeddial-top-left,.speeddial-top-right,
.speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right,
.speeddial-bottom-left,.speeddial-bottom-middle,.speeddial-bottom-right{
display:none;
}
#speeddialWindow{background: #E1E9EA !important;}
.speeddial-container{
margin:0 !important;
border:0 !important;
}

}
