css:
@-moz-document url-prefix("http://www.membrana.ru/particle/") {
.shadow-top, .shadow-bottom { display: none }
 #site-search { margin-right: 100px !important }



 .list-article-main { margin: 5px 0 5px 0 }
 .list-article-main>h3 { font-size: x-large !important }
 .list-article-main>.pic { width: 232px !important; left: 0 !important; float: right; margin: 0 !important }
 .list-article-main>.pic:after { background: none !important }
 .list-article-main>.pic>em:after { background: none !important }
 .list-article-main>.pic>em>img { width: 232px !important }

 .list-article, .hr { margin: 5px 0 5px 0 !important }
 .list-article>.pic { margin-left: 0 !important }
 .list-article>.pic:after { background: none !important }
 .shadow-top, .shadow-bottom { display: none }
 #site-search { margin-right: 100px !important }
.article-hd .title {margin: 0px !important; top: 0px !important;}
.pic-desc {width: 250px !important;position:relative !important; top: -500px !important;left:-50px !important;font-size: 14px !important;display:none}
.article>div.pic:before {content: "Титульное изображение скрыто. Показать";padding-top:0px!important;cursor:pointer;text-decoration: underline;color:#990000}
.article>div.pic {padding: 20px 0 0 30px !important;overflow:hidden;display:block;height:24px !important;margin-right:100% !important;}
.article>div.pic>img {display:none;margin-top:10px;}
.article>div.pic:hover {height:100% !important;overflow:visible!important;}
.article>div.pic:hover>img {display:block !important;}
.title {width: 110% !important;padding-left: 30px;}
.a-comments {
opacity: 1 !important;
width: 98% !important;
margin-top:10px !important;}
.a-body {width: 98% !important;
padding: 0 15px !important;
opacity: 1 !important;
}

.controls {display:none}
.rel-pic img{
height: 60px !important;}
.content-wrapper {background: none !important;}
.aside {
    width: 80% !important;    margin-bottom: 0px !important;}
.active {position:absolute !important;left:200px;top: 8px;}
.nav {
    margin: 0 0 5px !important;
}
.pic {width: auto !important;margin-right: 200px;min-width: 320px;}
.articletags {padding-bottom: 50px !important;position:relative;margin-bottom: 0px !important;  }
.articletags:after {content: "Схожее:";position:absolute; top:30px;left:0px;color: #000; font-size: 30pt;font-weight: bold;}
.list-article-related .rel-pic {
    margin: 0 0 3px !important;
    position: relative;
}
.a-comments:before {content: "Комментарии:";
margin: 0px 10px 0px 15px !important;color: #000; font-size: 30pt;font-weight: bold;}
 .nav>li>a {
font: bold 13px Tahoma,sans-serif !important;
    margin: 0 0 0px 15px !important;
    text-transform: uppercase;
margin-top: 5px !important;
border-bottom: 1px dashed #fff;
padding: 0 !important;
}
 .nav>li.active>span>a {border-bottom: 1px dotted #eee;margin-top: 1px !important;}
 .nav>li.active>a {font-size: 10px !important;border-bottom:none;}
.nav li a:hover {color: #00A1FF !important;}
 .nav>li {margin-top: 5px !important;}
 .body { width: 100% !important; margin: 0 !important }
 .content, #a-wrapper { width: 100% !important }
 #a-wrapper { left: 0 !important }
 .a-wrapper, .a-wrapper-ii { background: 540px 0 !important }
 .a-wrapper-i { background: 500px 100% !important }
 .a-body>.image { text-align: center }

 .list-article-related { text-align: left }
}
