css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

#bar-deviant a{ color:#FF99FF !important; }
#bar-deviant{ color:#CC66CC !important; background-color: #CC0066 !important; border: 0px !important; }
.alt0{ background-color: #FF66CC !important; } 
.alt1{ background-color: #CC0066 !important; } 
.aside-left, .damncr .userinfo .avatar{ background-color: #000000 !important; border: 0px !important; } 
.damn-lo-row-top{ background-color: #000000 !important; border: 0px !important; } 
.damncr-infoctr { color: #CC3366 !important; border: 0px !important; border: 0px !important; } 
.damncr-infoctr a:hover{ color: #993366 !important; } 
.damncr-infoctr a{ color: #CC3366 !important; } 
.damncr-title a:hover{ color: #FF66CC !important; } 
.damncr-title a{ color: #CC66CC !important; } 
.damncr-title{ background-color: #CC0066 !important; color: #FFFFFF !important; border: 0px !important; } 
.damncrc-chat .join, .damncrc-chat .part{ color: #9966FF !important; } 
.damncrc-chat .other-hl{ background-color: #CC0066 !important; } 
.damncrc-chat .self-hl{ background-color: #330000 !important; } 
.damncrc-chat .userinfo-inner .avatar, .damncrc-chat .userinfo-inner .bodyarea{ background: none !important; background-color: #CC3399 !important; border: 1px !important; } 
.damncrc-chat .userinfo-inner div.read a:hover{ color: #666699 !important; } 
.damncrc-chat .userinfo-inner div.read a{ color: #FF99FF !important; } 
.damncrc-chat .userinfo-inner div.read{ color: #000000 !important; background-color: #993366 !important; border: 0px !important; } 
.damncrc-chat a:hover{ color: #666699 !important; } 
.damncrc-chat a{ color: #663333 !important; } .damncrc-chat span{ background: transparent !important; color:#FF99FF !important; border: 0px !important; } .damncrc-chat{ background: #000000 !important; color: #9966CC !important; border: 0px !important; } 
.damncrc-chat-window { border:none !important; } 
.damncrc-error{ background-color: #993366 !important; color: #CC0066 !important; border: 0px !important; } 
.damncrc-topic a:hover{ color: #9966FF !important; } 
.damncrc-topic a{ color: #CC3366 !important; } 
.damncrc-topic{ background-color: #FF66CC !important; color: #000000 !important; border: 0px !important; } 
.main span { color:#FF99FF !important; border: 0px !important; } 
.sidebar dl.simple dt { color:#CC0066 !important; border: 0px !important; } 
.tabbar a{ background-color: #CC0066 !important; border: 0px !important; } 
.tabbar a[style*=\"black\"]{ color: #FF99FF !important; background-color: #666699 !important; border: 0px !important; } 
.tabbar a[style*=\"maroon\"]{ background-color: #663333 !important; border: 0px !important; } 
.tabbar strong{ background: #FFCCFF !important; border: 0px !important; } 
div#body { background-color: #000000 !important; border: 0px !important; } 
dl.simple dd, dl.flat dd { line-height:1.2em !important; color:#9966CC !important; border: 0px !important; } 
input.button:hover{ color: #993366 !important; } 
input.button{ -moz-border-radius: 0 !important; background-color: #CC0066 !important; border: 0px !important; color: #9966FF !important; font-weight: bold !important; font-size: 10px !important; text-transform: uppercase !important; padding: 3px !important; margin: 0px !important; }

}
