css:
/*	
	Global - plain, as plain can be
	by The Pompitous of Love
	Version 1.0
	Resets font face, color, background color, text alignment, 
	float on all div's, hides all images - just makes everything PLAIN!
*/

/*	Set foreground color to black on everything but anchor:	*/
body, div, span, h1, h2, h3, h4, h5, h6, p, blockquote, pre, abbr, acronym, address, big, cite, code, del, dfn, em, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, legend, table, caption, tbody, tfoot, thead, tr, th, td, thead th, tbody th {
	color: black !important;
}

/*	Set foreground color on anchor to standard blue:	*/
a {
	color: blue !important;
}
a:link {
	color: #006400 !important;
}
a:visited {
	color: #DC143C !important;
}


/*	Set page and font colors for ALL visible tags:	*/
body, div, span, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, legend, table, caption, tbody, tfoot, thead, tr, th, td, thead th, tbody th {

	background-image: none !important;
	background-color: white !important;

	font-family: serif !important;

}

/*	Set font size for ALL visible tags except headings:	*/
body, div, span, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, legend, table, caption, tbody, tfoot, thead, tr, th, td, thead th, tbody th {

	font-size: 15pt !important;
	line-height: 18pt !important;

}

/*	Set width for top-level tags:	*/
body {

	width: 100% !important;

}

/*	Set layout for block-level tags:	*/
body, div, p, blockquote, pre, code, dl, dt, dd, ol, ul, li, form, table, tbody, tfoot, thead, tr, th {

	float: none !important;
	height: inherit !important;
	width: inherit !important;
	min-height: inherit !important;
	min-width: inherit !important;
	text-align: left !important;

}

/*	Padding of content tags:	*/
div, p, pre, code, dl, ol, ul, form, table {
	
	margin: 0px !important;
	padding-top: .5em !important;
	padding-bottom: .5em !important;
	padding-left: .5em !important;
	padding-right: .5em !important;

}

/*	Tables:	*/
table {
	border-color: #aaa;
}

td {
	
	margin: 0px !important;
	padding-top: 4px !important;
	padding-bottom: 4px !important;
	padding-left: 4px !important;
	padding-right: 4px !important;

}

img {
	display: none !important;
}
