css:
namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.computer-juice.com/forums/"), url-prefix("http://www.computer-juice.com"), url-prefix("http://www.computer-juice.com/forums/f10/") {


* {
background: #262626 !important;
border-color: #3e3e3e !important;
color: #8a8a8a !important;
text-shadow: none !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
}

div.res h3 a.yschttl,
a:link {
color: #3098cd !important;
}

b,
b *,
strong,
strong *,
a:hover {
color: #267eac !important;
}

h1,
h1 *,
h2,
h2 *,
h3,
h3 *,
h3 cite,
h4,
h4 *,
h5,
h5 *,
h6,
h6 *,
div.res h3 a.yschttl:visited,
pre,
a:visited {
color: #dcae21 !important;
}

span.url,
cite,
cite * {
color: #af5f00 !important;
}

li,
i,
i *,
em,
div.res a[href*="http"] {
color: #dcae21 !important;
}

input,
button,
select,
textarea {
background: #262626 !important;
border: 1px solid #3e3e3e !important;
}

a.sa-drawer {
background: #3e3e3e !important;
}

.ico,
.sm-media,
img[alt="toggle search suggestions"],
span.indicator,
span.video-icon,
img[src*="ygmabtlogoimg"],
img[src*="ygmalogoimg"],
img[src*=".gif"],
img[src*="logo"], 
img[src*="icon"], 
img[src*="folder"] {
display: none !important;
}


}
