css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){

.speeddialThumb {
margin:8px !important;
border:1px rgba(0,0,0,0.2) solid;
border-radius: 6px !important;
padding:10px;
background:#fff ;
box-shadow: 0px 1px 1px rgba(10,10,10,0.1);

/* created by treasury */
}

.speeddialThumb:hover{
background:-moz-linear-gradient(rgba(34,111,154,1), rgba(63,132,174,1), rgba(51,102,154,1), rgba(51,102,154,1), rgba(51,102,154,1),rgba(51,102,154,1),rgba(51,102,154,1)) !important; 
}
.speeddial-top-middle {text-align: center;}
.speeddialThumb:hover .speeddial-top-middle{color: #fff !important;}
.speeddial-top-middle img {display: none !important;}
#speedDialPanel1-rows {padding-top: 0px}
.speeddial-top-middle {background: none !important;position:relative !important; left: 200px !important;}

 .speeddial-top-left,.speeddial-top-right,
.speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right
{
display:none;
}
#speeddialWindow{background: #f1f1f1 !important;}
.speeddial-container{
margin:0 !important;
border:0 !important;
}

}
