css:
#navigator-toolbox {
background: -moz-linear-gradient(top, rgba(255,255,255,.0), rgba(255,255,255,.8)) !important;}

#navigator-toolbox:not([blah]) .tabbrowser-tab
	{color: black !important; 
         text-shadow: white 0 0 9px, 
                      white 0 0 9px, 
                      white 0 0 9px, 
                      white 0 0 9px, 
                      rgba(255, 255, 255, 1.0) 0 0 15px !important; }

.tabbrowser-tab:not([selected="true"]) {
background-image: -moz-linear-gradient(top, rgba(255,255,255,0), rgba(255,255,255,.5)) !important;}

.tabbrowser-tab:not([selected="true"]):hover, .tabs-newtab-button:not([selected="true"]):hover {
background-image: -moz-linear-gradient(top, rgba(255,255,255,.5), rgba(255,255,255,.5)) !important;}

.tabbrowser-tab, .tabs-newtab-button
{background-image: -moz-linear-gradient(top, rgba(255,255,255,0), rgba(255,255,255,.5)) !important;}
