css:
@-moz-document url-prefix('http://www.google.com/calendar/'), url-prefix('https://www.google.com/calendar/') {

/*		Hides           */
/************************/

rule_to_fade { opacity: 0; -webkit-transition: opacity 1s 0.2s }

rule_to_show { opacity: 1 }

/* Top navigation bar */ #gb,
/* Logo */ .domainlogoparent,
/* Control bar 4 modes */ #vr-proto-nav,
rule_to_hide { display: none !important; }

rule_to_hide_borders { border: 0 !important }

rule_to_hide_backgrounds { background: none !important; }

/* Special 
******************/

#vr-proto-header { height: 10px; -webkit-transition: height 1s 1s; overflow: hidden }
#vr-proto-header:hover { height: 70px }
#vr-proto-header > * {  }

#mainbody { padding-top: 0 }
#nav { margin-left: 10px }
#mainbody { margin-left: 170px }

}
