css:
@-moz-document url-prefix('https://www.google.com/calendar') {
  /* Current day highlight */
  .tg-today,
  .st-bg-today, .st-bg-td-first, .st-bg-td-last,
  .dp-today-selected { background-color: #FFF7D7 !important; }
  
  /* Tasks color */
  .rb-n { color: #FFFFFF !important; }
  
  /* Events text */
  .chip { color: #FFFFFF !important; }
  
  /* Event resizer icon */
  .rszr-icon-hover { border-color: #FFFFFF !important; }
  
  /* Hour labels background */
  .tg-times-pri,
  .tg-times-sec {
    background-color: #F1F1F1 !important;
  }
}
