css:
@-moz-document domain("sporu.net") {
.banner, .top_banner, .left_banner, .adver, #ad_ph_1 {
  display: none !important;
}
.header_wrapper {
  top: 0px !important;
}
.menu_wrapper {
  top: 271px !important;
}
.item {
  padding-top: 0px !important;
  text-align: left;
  width: 680px;
}
.item div {
  padding-bottom: 0px !important;
  margin-top: 0px !important;
}
}

@-moz-document url-prefix("http://sporu.net/new/"), url-prefix("http://www.sporu.net/new/") {
form table tr td:nth-child(1) {
  font-size: 14px !important;
}
textarea {
  width: 682px;
}
.content div > table {
  width: 220px !important;
}
.content div > form > table {
  table-layout: fixed !important;
  overflow: hidden !important;
}
.content div > table > tbody > tr > td > div > img {
  display: none !important;
}
}
