css:
@-moz-document domain('news.ycombinator.com'), domain('hackerne.ws') {
.subtext, img, td[bgcolor="#ff6600"], .yclinks { display: none }
table[bgcolor="#f6f6ef"] { border-top: 15px solid #ff6600 }
form[method="get"] { position: fixed; bottom: 0; right: 0; }
.title a { font-size: 120% }
td[align="right"].title { display: none }
.title { padding: 5px; }
}
