css:
@-moz-document domain('www.mail.com') {

/* Hide things. */
body > *, #container > *, #container > .header-wrapper > *, #container > .header-wrapper > .header > *
{ display: none !important; }

/* Unhide some of the things. */
#container, #container > .header-wrapper, #container > .header-wrapper > .header, #container > .header-wrapper > .header > .login, #container > .header-wrapper > .header > a
{ display: block !important; }


/* Style some of the things. */

.homepage #container > .header-wrapper > .header
{ width:100% !important; }

#container > .header-wrapper > .header > .login
{ padding:8px 1em 0 0 !important; }

#container > .header-wrapper > .header > .login .login-box input.text, #container > .header-wrapper > .header > .login .login-box input.password
{ border-radius:3px; }

#container > .header-wrapper > .header > a
{ padding-left:1em !important; }

#container > .header-wrapper > .header > .login > .login-box
{ background:transparent none !important; }

}
