css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pcmag.com") {

body, #wrapperPage, #w_contentGroup, #w_contentCol2, #articleContent, .articleCopy, .articleTitle
{ 
   width: 100% !important;
   max-width: none !important;
   margin: 0 !important;
   padding: 0 !important;
   float: none !important;
}

#w_header, #w_adModule, .clearb, #w_footer, #w_subHead, #w_contentCol1, #underArticleLinks, #marketplace, #newsletters, #graveyard, .clearr, .highlights_content
  { display: none ! important; }

body * { clear: none !important; }

.articleCopy
{
   padding: 2% !important;
   width: 96% !important; 
   text-align: justify !important;
   font-size: 12pt !important;
   font-family: arial, sans-serif !important;
   max-width: none !important;
/*
   position: absolute !important;
   left: 0 !important;
*/
}

}
