css:
@-moz-document domain('megaplan.ru'){

.toolbar,.wdt,textarea,#leftColumnBg,.arrows,#menuMain,#leftColumnMain,body,ul,li{background-color:#222!important;color:#CCC!important}

.sdf-wnd-tbl,.sdf-wnd-c,.inner,.sortdesc,.switch,.tag-checked,.selected,.informer-left,th,.task-statement,.attaches,#commentForm,.topic-content{
background-color:#333!important;color:#CCC!important
}

a{
color:#AA8!important;
font-weight: bold!important;
}

tr:hover
{
background-color:#555!important;
}

.sdf-wnd-br,.sdf-wnd-b,.sdf-wnd-bl,.sdf-wnd-r,.sdf-wnd-l,.sdf-wnd-tr,.sdf-wnd-tl,.sdf-wnd-t,.informer-center,.bg,.informer-left,.ico,i,.b{
visibility:hidden!important;
}

span{
color:#CCC!important
}

.sdf-wnd-tbl{
color:#000!important;
}

table{
border:1px solid grey!important;
}

.informer,.menu-wrapper-outer{
opacity: 0.4!important;
background-color:#000!important;
}

div{
border-color: black!important;
}
}
