css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("e0166.blog89.fc2.com") {

#secondary-column,
#secondary-column2,
#branding table[align], /* Site description */
.entry-body + .entry-body, /* Related entries */
.fc2_footer, /* Web Hakushu, FC2 Bookmark */
.entry-body > div > a[href$="/e0166"], /* Feed links */
.entry-body > a[target="_blank"], /* SBM links */
.entry-body > a[href^="javascript:"], /* newsing */
iframe /* ads */
{
    display: none !important;
}

/* to liquid */
#container {
    background-image: none !important;
    width: 95% !important;
}

#branding,
#hajime10,
#primary-column {
    width: 100% !important;
}

}
