@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://shine.net/forum") {

#header, #flasharea, #right {display:none;}

#main, body {background:#fff!important;}

#main_text {display:block!important; width:auto!important;}

#ForumThreadList, .forumPostDate, #ForumThreadTitle {width:auto!important;}
#ForumThreadListHeader {width:980px!important;}

.threadlistitem, #ForumThreadView {width:960px!important;}
.threadlistheader, #ForumThreadHeader {background:#aaa;}
.threadlisttitle {width:543px!important;}

a {color:#333!important;}

#ForumThreadHeader, #ForumThreadTopButtons, #ForumThreadBottomButtons, #ForumThreadListUpperButtons, #ForumThreadListLowerButtons, #ForumThreadBottomButtons, #ForumThreadTopButtons {width:960px !important; padding-left:0!important; background:#888!important;}

#ForumThreadSocialButtons {width:955px!important; background:none!important;}

.forumposttext {width:744px!important;}
.threadpost {width:960px !important; padding-left:0!important;}


}
