css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

    /* ::::: Safari Style Tabs for Firefox 4+ ::::: */ 

    #main-window[tabsintitlebar="true"]:not([inFullscreen]) .tabbrowser-tab,
    #main-window[tabsintitlebar="true"]:not([inFullscreen]) .tabs-newtab-button{
    background-position: 1px 0px !important;
    -moz-border-image: url(data:image/png;base64,) 2 3 2 / 2px 3px 2px repeat stretch !important;
    border-radius: 0 0 7px 7px !important;
    }

    #main-window[tabsintitlebar="true"]:not([inFullscreen]) #nav-bar,
    #main-window[tabsintitlebar="true"]:not([inFullscreen]) #TabsToolbar{
    background: none !important;
    }
