css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gpokr.com") {

.gwt-InlineLabel { display:none; }
.iogc-PlayerStatsPanel:nth-child(2) { display:none; }
.iogc-PlayerStatsPanel:nth-child(3) { display:none; }
.iogc-PlayerPanel-stat { font-size:150% !important; }

.iogc-PlayerPanel-outer:hover .gwt-InlineLabel { display:block; }
.iogc-PlayerPanel-outer:hover .iogc-PlayerStatsPanel { display:block; }
.iogc-PlayerPanel-outer:hover .iogc-PlayerPanel-stat { font-size:100% !important; }

}
