css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); 

#appmenu-button  {
	min-width: 70px !important;
	padding: 0 5px 0 0 !important;}

#main-window:-moz-window-inactive #appmenu-button:not(:hover) {
	background: transparent !important;
	box-shadow: 0 0 0 1px rgba(255,255,255,.25) inset !important;}

#appmenu-button .button-text {
	display: none !important;}

#appmenu-button .button-icon {
	list-style-image: url("chrome://branding/content/icon48.png") !important;}

@media all and (-moz-windows-default-theme) {
	#appmenu-button .button-icon {
		-moz-image-region: rect(4px 32px 29px 0px) !important;}
	#main-window[sizemode="maximized"] #appmenu-button .button-icon {
		-moz-image-region: rect(5px 32px 23px 0px) !important;}}

@media all and (-moz-windows-compositor) {
	#appmenu-button .button-icon {
		-moz-image-region: rect(4px 32px 22px 0px) !important;}
	#main-window[sizemode="maximized"] #appmenu-button .button-icon {
		-moz-image-region: rect(5px 32px 22px 0px) !important;}}

#appmenu-button dropmarker {
	margin: 1px 0 0 1px !important;}
