css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hyper-text.org") {

#content_body > .sub_menu,
#content_body > .ex_contents,
#contents > .footer_area > .recommended_cd,
#contents > .footer_area > .playlist,
#contents > .footer_area > .flickr,
#contents > .footer_area > .footer > ul.icon_list,
#content_body dl.add_bookmarks_block,
dl.adsense
{
    display: none !important;
}

/* To liquid */
#contents {
    width: 90% !important;
}

#main_contents {
    width: auto !important;
    max-width: 900px !important;
}

#main_contents h1 {
    width: 285px !important;
}

#blog_author {
    margin-right: 15px !important;
    float: right !important;
}

#content_body,
#content_body > .blog,
#contents > .footer_area {
    width: auto !important;
}

fieldset label + * {
    width: 70% !important;
}

/* Fixed backgrounds */
#main_contents {
    background-position: left top !important;
}

#content_body {
    background-position: right bottom !important;
}

fieldset p.required,
fieldset p.submit {
    background-color: transparent !important;
}

/* Fixed footer texts color */
.footer_area:hover > .footer,
.footer_area:hover > .footer a {
    color: #ccc !important;
}

}
