css:
@-moz-document url-prefix("https://www.youtube.com/watch"), url-prefix("http://www.youtube.com/watch")
{

#watch7-playlist-tray-container,
.watch7-playlist-bar-secondary-controls{
    display:none!important;
    }

html{
    overflow:hidden!important;
    }

#player-api{
    padding:0!important;
    width:100%!important;
    height:100%!important;
    position:fixed!important;
    top:0!important;
    right:0!important;
    bottom:0!important;
    left:0!important;
    z-index:10000;
    }
    
.watch-sidebar{
    padding:0!important;
    position:fixed!important;
    top:auto!important;
    right:auto!important;
    bottom:0!important;
    left:50%!important;
    z-index:10001;
    overflow:hidden!important;
    max-width:100px!important;
    min-width:100px!important;
    width:100px!important;
    margin-left:-50px!important;
    }
    
#watch7-playlist-bar-controls{
    border:0!important;
    overflow:hidden!important;
    margin-bottom:-9px!important;
    padding:0!important;
    text-align:center!important;
    font-size:12px!important;
    line-height:22px!important;
    height:30px!important;
    }
#watch7-playlist-bar-controls a,
#watch7-playlist-bar-controls img{
    padding:0!important;
    margin:0!important;
    vertical-align:top!important;
    }

}
