css:
@-moz-document domain(gonintendo.com){
  #header { height:0 !important; }
  #banner { height:20px !important; top:0; }
  #banner, #mainnav { position:fixed; left:0; right:0; z-index:3; }
  #logobanner { border:none !important; }
  #logo { 
    height:16px !important;
    background-image:none !important;
    position:relative !important;
    top:-8px !important;
  }
  #logo:before {
    content:url(data:image/png;base64,)
  }
  #logo:after {
    content:"gonintendo";
    font:12px/1.3 "Lucida Grande",Arial,Helvetica,sans-serif normal;
    font-variant:small-caps;
    color:white;
    position:relative;
    top:-4px; left:4px;
  }
  #logo > img { display:none; }
  #logo:hover, #logo:active { text-decoration:none !important; }
  #bannerad { display:none; }
  #channelbar { padding:0 !important }
  #channelbar > div.floatleft { display:none; }
  #channelbar > span.floatright {
    position:absolute;
    top:4px;
    width:980px;
    text-align:right;
  }
  #mainnav {
    top:20px;
    height: 24px !important;
    background-position:0 -2px !important;
  }
  #mainnav > div, #mainnav > div > div {
    height:24px !important;
    /* fix for dark theme */
    border:none !important;
  }
  /* QUICKIE SEARCH START */
  #rightcol > h3:nth-child(3), .gsc-search-box, #rightcol a.search-link {
    position:fixed;
    z-index:3;
  }
  #rightcol > h3:nth-child(3) {
    width:auto !important;
    margin-left:-855px !important;
    top:0 !important;
    border:none !important;
    color:white !important;
    font-weight: normal !important;
    font-size:10px !important;
    padding-top:1px !important;
  }
  .gsc-search-box {
    margin-left:-397px !important;
    width:150px !important;
    top:0;
  }
  .gsc-input-box { height:12px !important; }
  .gsc-input, .gsib_a, .gsib_b, .gsst_a, .gsst_b, .gsc-search-button {
    padding:0 !important;
  }
  .gsc-input { margin-top:1px !important; }
  .gsib_a, .gsib_b { line-height:0px !important }
  .gsc-input { height:10px !important; }
  .gsc-completion-container { font-size:10px !important; }
  /* QUICKIE SEARCH END */
  #rightcol a.search-link { margin-left:-625px !important; top:5px; }
  #nav a {
    height:24px!important;
    text-align:left !important;
    padding-right:0 !important;
    padding-left:4px !important;
  }
  .nav-parent > a {  width:auto !important; margin-right:1ex !important; }
  #topper { display:none !important; }
  #wrapper {
    margin:44px auto 0 auto !important;
    padding:1em 0 !important;
    background:none !important;
    border:none !important;
  }
  #wrap { margin-top: 44px !important; padding-top: 1em !important; }
  #leftcol { padding:0 !important; width:100% !important; margin-right: 0 !important; }
  #announcements, #live-stream { display:none !important; }
  #blognav {
    position:fixed !important;
    top:21px;
    margin-left:290px !important;
    z-index:4;
    opacity:0.5;
  }
  #blognav div {
    background-size:344px auto !important;
    width:auto !important;
    white-space:nowrap !important;
    max-width:67px !important;
    padding: 0 4px !important;
    height:24px !important;
    background-repeat:no-repeat !important;
  }
  #blognav div.first { background-position:0 2px !important; }
  #blognav div.first-active { background-position:0 -20px !important; }
  #blognav div.second { background-position:-81px 2px !important; }
  #blognav div.second-active { background-position:-81px -20px !important; }
  #blognav div.third { background-position:right 2px !important; }
  #blognav div.third-active { background-position:-162px -20px !important; }
  #blognav div.fourth, #blognav div.fourth-active { display:none; }
  div.story { margin-bottom:1em !important; }
  div.story:nth-last-child(2):not([class~=page-jump-links]) { display:none !important; }
  div.story > div.story-leftcol { width:auto !important; max-width:800px !important; }
  div.story { border-left: none !important; }
  div.story-content > div.story-leftcol {
    width:960px !important;
    clear:left !important;
    padding-right:10px !important;
  }
  /* SEARCH PAGES START */
  div.story > h1.storytitle, div.story > div.storymeta {
    margin:0px 5px !important;
  }
  /* SEARCH PAGES END */
  #rightcol { width:0 !important; overflow:hidden !important; padding:0 !important; }
  #wrapper > div.story { display:none !important; }
  #footer {
    border-radius:0 !important;
    position:fixed !important;
    bottom:0 !important;
    z-index:2;
  }
}

@-moz-document url-prefix(http://gonintendo.com/?mode=viewstory),
               url-prefix(http://www.gonintendo.com/?mode=viewstory) {
  #leftcol > div.story:nth-child(4) { display:none !important; }
  #comments { width:100% !important; }
}

@-moz-document url-prefix(http://gonintendo.com/board),
               url-prefix(http://www.gonintendo.com/board) {
  #channelbar {
    text-align:right;
    margin-bottom:-18px !important;
    padding-top:5px !important;
  }
}
