css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dailykos.com") {
/* userstyles */

.ct p, #intro,
#intro ol li,
#intro ul li,
#intro p,
.article-body blockquote ol li,
.article-body blockquote ul li,
.article-body p {
color: black !important;
/* increase the font size
font-size: large !important; */
}

.ct blockquote,
.article-body #intro blockquote,
.article-body blockquote {
color: maroon !important;
/* increase the font size and alter font family
font-size: large !important;
font-family: serif !important;*/
}

p.sig {color: #303000 !important;}

h4 {color: #585752 !important;}

h5,
#storyWrapper .article .meta .date {color: #450D21 !important;}

}
