css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("index.hu") {

    .travelo-doboz, .velvet-doboz { display: none !important; }
    
    .cikk_also_ajanlo .likegomb,
    .pp-search + div > a[href^="#"], #shopline, #also_bannerek {
       display: none !important;
    }
  
    .cikk-navigation {
       margin-bottom: 5px!important; 
    }
    .date-share { margin: 5px 0 !important; }
    

    /* index 2 */

    #social_wrapper .sziget {
        background-color: #FAFAFA !important;
        border: 1px solid #EEEEEE !important;
        box-shadow: none !important;
    }
    
    #social_wrapper .stamp .blog, #social_wrapper .blogketrec_title, #social_wrapper .feed_title {
        color: #133D70 !important;
    }
    #social_wrapper .stamp .salestag.bulvar { background-color: #E7527A !important; }
    #social_wrapper .stamp .salestag.zene { background-color: #FF9C50 !important; }
    SPAN.adult { background-color: #EC5454 !important; }

}


@-moz-document 
    domain("index.hu"), 
    domain("sportgeza.hu"), 
    domain("travelo.hu"),
    domain("velvet.hu"),
    domain("divany.hu"),
    domain("totalcar.hu")   { 
    
    li._iph_site_index2 a,
    li._iph_site_index a{
        font-weight: bold !important;
        background-color: #f9f9f9;
    }
    
    li._iph_site_totalcar {
        text-transform: uppercase;
    }
    li._iph_site_shopline, li._iph_site_travelo { 
        display: none !important;  
    }
    
    div[style^="position: absolute; width: 1px; height: 1px;"],
    object[data^="http://ad."],
    .goAdverticum, .hirdetes_bottom, .cikk-inline-ad,
    .shopline-box, .travelo-box, div[id^="goa-layer"],
    .banner300, .hirdetes-center,
    iframe[src^="http://ad."], iframe[src*="zona"],
    #rightsponsor, #rightsponsor + div > a[href^="#"],
    #right_szponzor, #right_szponzor + div > a[href^="#"] {
       display: none !important;
    }
    
}
