css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
body{
  background:#002b36 !important;
  color:#839496
}
.organic-listing, .side, .footer-parent, #header-img, .rank{
  display:none;
}
.title a, .tagline{
  color:#93a1a1!important;
}
.title a:visited{
  color:#586e75!important;
}
.domaina{
  color:#2aa198 !important;
}
li a{
  color:#b58900!important;
}
.subreddit{
  color:#cb4b16 !important;
}
.md{
  background:#002b36!important;
  border:0px solid #268bd2!important;border-radius:10px!important;
  padding-left:5px!important;
}
.comment .child{
  border:none!important;
}
#header{
  background:none!important;border:none!important;
}
.tabmenu li a, .redditname a{
  background:none!important;
  color: #d33682!important;
}
.tabmenu li.selected a{
  border:none!important;font-size:x-large!important;
}
#header-bottom-right, #sr-header-area{
  background:none!important;
}
#sr-header-area a{
  color:#cb4b16!important;
}
}
