css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.basecamphq.com") {

}

#Header {
    background-color: #000000 !important;}

#Header div.navigation a:link, #Header div.navigation a:visited {
    background-color: #000000 !important;}

div#Tabs a:link, div#Tabs a:visited {
    text-decoration: none;
}

#Tabs a:link {
    background-color: #222222 !important;
    color: #FFFFFF !important;}

#Tabs a:hover{
    background-color: #333333 !important;
    color: #FFFFFF !important;}

#Tabs a:visited {
    background-color: #222222 !important;
    color: #CCCCCC !important;}

#Tabs a:active {
    background-color: #222222 !important;
    color: #000000 !important;}

#Header div.navigation, #Header div.navigation a:link, #Header div.navigation a:visited {
    color: #FFFFFF !important;}

#Tabs ul#MainTabs a.current:link, #Tabs ul#MainTabs a.current:visited {
    color: #222222 !important;}
