css:
@-moz-document domain('reddit.com'){

#sr-header-area { padding: .5em 1.25em !important; }
.separator { color: #ccc !important; }
.tabmenu { height: 1.25em !important; }
.tabmenu li { padding: .25em .5em .125em .5em !important; }
.tabmenu li a { padding: .25em 1em .125em 1em !important; border-radius: .25em .25em 0 0; }
.side { margin-top: 1em; margin-right: 1em; }
.content { padding: 0 .5em .5em .5em !important; }
.infobar { opacity: .75; margin-bottom: 1em; margin-right: 315px !important; display: none; }
.md { max-width: 100% !important; }
#login_login-main { border: none !important; background-color: #eaeaea !important; padding: .5em 0 !important; margin: 1em 0 !important; }
#header-img { height: 25px !important; margin: .5em 1em; }
.organic-listing { display: none; }
.link { margin: 1.5em 315px 1em 0 !important; padding: .5em !important; }
.link:nth-child(4n+1) { background-color: #f0f0f0 !important; }
.rank { display: none; }
.midcol { margin: 0 1em 0 .5em !important; }
.thumbnail { margin: 0 1em 0 0 !important; }
.title:link, .title:active { color: #258; }
.title:visited { color: #999 !important; }
.title:hover { color: #036; text-shadow: 1px 1px 0 #ccc; } 

}
