css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(www.tokyotosho.info) {
* {
    font-size:8px!important;
}
body {
    background:#1b1b1b!important;
    color:#eee!important;
    font-family:'PF Tempesta Seven'!important;
}
a {
    color:#de8c8c!important;
    font-family:'PF Tempesta Seven'!important;
    text-decoration:none!important;
}
a:hover {
    color:#a16a71!important;
    text-decoration:underline!important;
}
td.stats {
    font-family:'PF Tempesta Seven'!important;
}
input[type='text'],
select {
    background:#262626!important;
    border:1px solid #131212!important;
    color:#eee!important;
    height:18px!important;
    padding:1px 3px!important;
    -moz-appearance:none!important;
    -moz-box-sizing:border-box;
}
span[style*="color: green"]
{
    color:#93c78c!important;
}
span[style*="color: orange"]
{
    color:#c16780!important;
}
span[style*="color: red"]
{
    color:#93c78c!important;
}
select {
    padding:1px 0!important;
}
#main {
    background:#222!important;
    border-color:#131212!important;
    margin:0 auto;
}
#main>h1 {
    margin:0.6em 0 0.2em;
}
.shade {
    background:#262626!important;
}
td[colspan='3'] {
    border:0!important;
}
.gsc-control-cse,
p.centertext>a,
.footer,
.news,
#main>h2,
#main>h3,
#main>form:last-child:not([name='rss']) {
    display:none;
}
tr[style*='background-color:'] {
    background-color:#de8c8c!important;
    height:2px!important
}
}
