@namespace url(http://www.w3.org/1999/xhtml);

/* 
 * Highlight Links and Submits on Click
 * This file is licensed under Creative Commons Attribution License
 * http://creativecommons.org/licenses/by/3.0/
 *
 * Initial Developer:
 * Norman Carpenter (htmlapps@html-apps.com)
 *
 */

@-moz-document url-prefix(http), url-prefix(https), url-prefix(file) {
   a:active, input[type="submit"]:focus
   {
      background-color: rgba(255,204,102,0.75) !important;
   }
   a:active, input[type="submit"]:focus
   {
      border: 3px solid rgba(255,153,00,0.75) !important;
   }
   a:active, input[type="submit"]:focus
   {
      -moz-box-shadow: 0px 0px 5px 5px rgba(255,204,00,0.75) !important;
   }
   a:active, input[type="submit"]:focus
   {
      box-shadow: 0px 0px 5px 5px rgba(255,204,00,0.75) !important;
   }
}
