css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvunderground.org.ru") {


/* === LiNks =======================
==================================== */

a:link, a:active {
    background-color: inherit;
    color: #BABABA !important;
    text-decoration: none !important;
}
a:visited {
    background-color: inherit;
    color: #B8CDB7 !important;
    text-decoration: none !important;
}

a:hover {
    background-color: inherit;
    color: #B0AB6B !important;
    text-decoration: none !important;
}

a:visited:hover {
    background-color: inherit;
    color: #C48A8A  !important;
    text-decoration: none !important;
}

/* ===  Zebra Table =======================================================
(Try Drak/Grey #262626 or Dark/Grey Light #3C3B3B or Color you want!) =====
=========================================================================== */

/* === Exemple ================================================================
.author-styles>tbody> tr:nth-child(2n+1) td {background: #262626 !important; }
or ============================================================================
.author-styles>tbody> tr:nth-child(odd) td {background: #ccc !important; }
.author-styles>tbody> tr:nth-child(even) td {background: #eee !important; } 
 ============================================================================== */

.text>form>table>tbody tr:nth-child(2n+1) td {background: #3C3B3B !important; }
.tabbertab>table>tbody tr:nth-child(2n+1) td {background: #3C3B3B !important; }
.seriestable>tbody tr:nth-child(2n+1) td {background: #3C3B3B !important; }
.text>table>tbody tr:nth-child(2n+1) td {background: #3C3B3B !important; }
.text table {
    text-align: left;
    width: 700px !important;
}


/* Rétrécir Tables  ====== */

.seriestable {
    width: 1100px !important;
}


/* SUPP ADDs === */

#ad {
    display: none !important;
}


/* === END === */

}
