css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.yahoo.com"), domain("mail.yahoo.net") {

.tabs li.unremovable {
    border-radius: 5px 5px 0 0 !important;
    margin-left: 5px !important;
}

.nav-bar .tabs {
    padding-left: 0px !important;
}

.panescroll .contant{
    position:relative;
}

#msg-preview, .tripane {
    box-shadow:inset #E0E0E0 0px 0px 5px !important;
}

#msg-preview, .list-view, .list-view-items  {
    margin-left:4px;
}

.list-view-items div {
    background:transparent;
}

.message-header {
    border-right: 1px solid #E0E0E0 !important;
    overflow: auto !important;
    position: fixed !important;
    box-shadow:#E0E0E0 0px 0px 5px !important;
    height:100px !important;
    width:75%;
}

.tripane .msg-body {
    margin-left:15px !important;
    margin-right:15px !important;
}

.tripane .inner {
    margin:0px;
    margin-top:115px !important;
}

#rail-resize-hori {
    box-shadow:#E0E0E0 0px 0px 5px !important;
}

#uh #search {
    border-radius: 5px !important;
}

#search {
    box-shadow:#FFF 0px 0px 7px !important;
}

.shaded .btn, #uhimage{
    box-shadow:#777 0px 0px 5px !important;
}

.shaded .btn:hover, #uhimage:hover {
    box-shadow:#000 0px 0px 5px !important;
}

}
