css:
/* 
 * @name 		Google Mail Minimizer
 * @website		http://userstyles.org/styles/51876
 * @description	Optimize Google Mail for smaller Screens by moving some Elements
 * @version		1.1
 * @date 		2014-11-13
 */

@-moz-document domain('mail.google.com') {
    /* remove right add bar */
    td.Bu.y3 {
        display: none;
    }

    /* make mail content wider */
    .if {
        margin-right: 0px !important;
    }

    .apN .hx {
        padding-left: 0px !important;
    }

    .hx .gs {
        margin-left: 10px !important;
    }

    .ao8 {
        padding-right: 0px !important;
    }

    div.h9 {
        margin-left: 0px !important;
    }

    /* minimize mail */
    .Wg {
        padding-top: 3px !important;
        padding-bottom: 3px !important;
    }

    .xY.apA, .xY.apE {
        padding-top: 2px !important;
        padding-bottom: 2px !important;
    }

    .xY.apy, .xY.apt, .xY.apx {
        padding-top: 2px !important;
        padding-bottom: 2px !important;
    }

    /* minimize top bar */
    .G-atb, .G-as7-atb, .G-MI {
        margin-top: 0px !important;
        padding-bottom: 4px !important;
    }

    .aki {
        padding-top: 0px !important;
    }

    /* minimize hangouts */
    .pt {
        width: auto !important;
    }

    div.akc.aZ6 > div.T0.pp.aj3 {
        margin-left: 0px !important;
    }

    div.nH.oy8Mbf.nn.aeN > div.akc.aZ6.ajm {
        margin-right: 8px;
    }

    div.aim {
        padding-left: 0px !important;
    }
}
