@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {

/* === FLICKR WIDESCREEN - SCRIPT - Commented ON v.8 === */
/* AJOUTER LES SUPP "BY" "FROM" TRANSPARENT === */

/* ================ */

/* SUPP (display none) - "NEW" ICON - === */
.New {
    display: none !important;
}
/* SUPP (transparent) - TXT "from"/by"  - Near USER NAME - POOL - === */
.PoolList {
    color: transparent !important;
}
#pool-photos.sm .meta {
    color:transparent !important; 
}
/* SUPP (transparent) - TXT "by" - Near USER NAME POOL PRESENTATION PAGE - === */
.group-content-wrapper #div_hold_photos.HoldPhotos #pool-photos.sm .meta {
    color: transparent !important;
}
/* SUPP (transparent) - TXT "by" - FAV - Near USER NAME - === */
#faves .fave .meta {
    color: transparent !important;
}

/* =================== */

/* "COMMENT ICONS - DARK AND GREY (Color and Size) - === */
 
/* ==== COMMENT ICON - RED ICON :
http://www.favicon.cc/favicon/674/495/favicon.png -
top: 37px !important;
 === */
[alt="Yay! You've commented on this photo!"] {
    background:  url(data:image/x-icon;base64,) no-repeat!important;
    border-radius: 5px 5px 5px 5px !important;
    height: 16px !important;
    padding: 0px 0px 0px 25px !important;
    position: relative !important;
    width: 0 !important;
margin-left:5px !important;
}
[alt="Yay! You've commented on this photo!"] a:hover {
    background:transparent !important;
    color: grey !important;
    text-decoration: none;
}

/* ==== NO COMMENT ICON - GREY - "http://www.favicon.cc/favicon/850/283/favicon.ico" - === */
[alt="Go ahead! Make my day! (by commenting)"] {
    background:  url(data:image/x-icon;base64,) no-repeat!important;
    border-radius: 5px 5px 5px 5px !important;
    height: 16px !important;
    padding: 0px 0px 0px 25px !important;
    position: relative !important;
    width: 0 !important;
margin-left:5px !important;
}
[alt="Go ahead! Make my day! (by commenting)"] a:hover {
    background:transparent !important;
    background-color: transparent !important;
    color: grey !important;
    text-decoration: none;
}

/* ==== RETRIEVING COMMENT ICON - BLACK/RED +FLECHE - === */
[title="retrieving"] { 
    background: url(data:image/x-icon;base64,) no-repeat!important;
    border-radius: 5px 5px 5px 5px !important;
    height: 16px !important;
    padding: 0px 0px 0px 0px !important;
    position: relative !important;
    width: 0 !important;
}
[alt="Go ahead! Make my day! (by commenting)"] a:hover {
    background:transparent !important;
}
 [title="retrieving"] a:hover {
    background-color: transparent !important;
    color: grey !important;
    text-decoration: none;
}

/* COUNT - === */
[id^="FCO-image-count"] {
    color: #7D3636 !important;
    z-index: 10 !important;
}



/* ====================== */

/* HOME PAGE - COMMENT ICON - === */
#toto-main #toto-home .act-thumb [id^="FCO-photo-"] {
    position:relative !important;
    margin-top:-74px !important;
    margin-left:48px !important;
    display:block !important;
}

#toto-main #toto-home #photos-from-box [id^="FCO-photo-"] {
    display: inline !important;
    position: relative !important;
    top: -13px !important;
    left:-20px !important;
    z-index: 100 !important;
}


/* - PHOTOSTREAM - COMMENT ICON / COUNT - === */
#flickr_nav.flickr_nav ul li a img[id^="FCO-image"]:not([id^="FCO-image-count"]) {
    border-radius: 5px 5px 5px 5px !important;
    height: 13px !important;
    left: 64.5% !important;
    padding-left: 24px !important;
    position: fixed !important;
    top: 30px !important;
    width: 0 !important;
}
#head-nav-bar.js_on [id^="FCO-image-count-"]{
    color: #7D3636 !important;
    left: 65.5%  !important;
    position: fixed !important;
    top: 32px !important;
    z-index: 10 !important;
}


/* ==== SQUARE ==== */

/* NEW - POOL/FAV)ARCHIVE - FLUID - SQUARE (sq) - THUMBs (space for SCRIPT "COMMENTED ON") - === */
#pool-photos.sq .pool-photo ,
#faves.sq .fave , 
#archives.sq .archive {
    margin-top: 30px!important;
margin-bottom: -15px!important;
    position: relative !important;
margin-left:-2px !important;
}
 
/* NEW - POOL/FAV)ARCHIVE - FLUID - SQUARE (sq) - COMMENT ICON / COUNT - === */
#pool-photos.sq [id^="FCO-image-"] ,
#faves.sq [id^="FCO-image-"] ,
#archives.sq [id^="FCO-image-"] {
    display: inline-block !important;
    position: relative !important;
    top: -1px !important;
    opacity : 0.5 !important;
}

#pool-photos.sq [id^="FCO-image-"]:hover ,
#faves.sq [id^="FCO-image-"]:hover ,
#archives.sq [id^="FCO-image-"]:hover {
    opacity : 1 !important;
}
#pool-photos.sq [id^="FCO-image-count-"] ,
#faves.sq [id^="FCO-image-count-"] ,
#archives.sq [id^="FCO-image-count-"]{
    height: 12px !important;
    left: -8px !important;
    margin-bottom: -8px !important;
    padding-left: 51px !important;
    position: absolute !important;
    top: 82px !important;
    width: 0px !important;
}



/* SETs "THUMBNAIL VIEW" - SQUARE (sq) - THUMBNAIL - COMMENT ICON / COUNT - === */
#ViewSet #setThumbs.sq .thumb img[id^="FCO-image-"]{
    top: 3px !important;
    margin-left:30px !important;
    position:relative !important;
}
#ViewSet #setThumbs.sq [id^="FCO-image-count-"]{
    top: -1px !important;
    margin-left:4px !important;
    position:relative !important;
}



/* YOURS CONTACTs - SQUARE (sq) - COMMENT ICON - === */
#contacts-thumbs-wrapper #thumbnails.sq .contact .thumb [id^="FCO-photo-"] {
    display: block !important;
    height: 24px;
    margin-top: -34px !important;
    position: relative !important;
    z-index:100 !important;
 position:absolute !important;
    top:-1px !important;
    left:15px !important;
}

/* YOURS CONTACTs THUMBNAILS - SQUARE (sq) + SMALL (sm) - COUNT - === */
#contacts-thumbs-wrapper #thumbnails.sq .contact a span[id^="FCO-image-count-"] ,
#contacts-thumbs-wrapper #thumbnails.sm .contact .thumb a span[id^="FCO-image-count"] {
    position:relative !important;
    top:33px !important;
    margin-left:5px !important;
}

/* ==== SMALL ==== */

/* POOL PRESENTATION PAGE - COMMENT ICON / COUNT -
.group-content-wrapper #div_hold_photos.HoldPhotos #pool-photos.sm .thumb a img[id^="FCO-image-"] 
 === */
.group-content-wrapper #pool-photos.sm .thumb [id^="FCO-photo-"]{
    margin-top:0 !important;
    margin-left:2px !important;
    display:block !important;
    position:absolute !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
.group-content-wrapper #pool-photos.sm a [id^="FCO-image-count-"] {
    display: inline-block !important;
    left: 17px !important;
    position: absolute !important;
    top: 0px !important;
}


/* POOL ok/ FAV ok/NEW CONTACTs Upload - SMALL (sm) - FLUID - COMMENT ICON / COUNT - === */
#pool-photos.sm [id^="FCO-photo-"] {
    border-radius: 5px 5px 5px 5px !important;
    position:absolute !important;
    display:block !important;
    margin-left:-8% !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#pool-photos.sm [id^="FCO-photo-"]:hover {
    border-radius: 5px 5px 5px 5px !important;
    opacity:1 !important;
}
#pool-photos.sm [id^="FCO-image-count-"] {
    position:relative !important;
    left:-12px !important;
    display:inline !important;
    top:-3px !important;
}

#faves.sm [id^="FCO-photo-"] ,
#archives.sm [id^="FCO-photo-"]{
    display: block !important;
    position: relative !important;
    top: 3px !important;
    left:-40% !important;
    z-index:100 !important;
    opacity : 0.5 !important;
}
#faves.sm [id^="FCO-photo-"]:hover ,
#archives.sm [id^="FCO-photo-"]:hover {
    opacity : 1!important;
}
#faves.sm [id^="FCO-image-count"] ,
#archives.sm [id^="FCO-image-count"]{
    display: inline !important;
    position: relative !important;
    top: -3px !important;
    left:-12px !important;
}


/* YOURS CONTACTs - SMALL (sm) - COMMENT ICON - === */
#contacts-thumbs-wrapper #thumbnails.sm .thumb [id^="FCO-photo-"] {
    display: block !important;
    top:-33px !important;
    position: relative !important;
    margin-right:20px !important;
    margin-bottom:-13px !important;
    left:-25px !important;
    position:relative !important;
}

/* ==== MEDIUM (md) - ==== */

/* POOL ok /FAVs /ARCH - MEDIUM (md) - FLUID - COMMENT ICON / COUNT - === */
#pool-photos.md [id^="FCO-photo-"]{
    display: inline-block !important;
    width:10px !important;  
line-height :20px !important;
top : 19px !important;
left: -25px !important;
position : relative  !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#pool-photos.md [id^="FCO-photo-"]:hover{
    opacity:1 !important;
}
#pool-photos.md [id^="FCO-image-count"] {
    display: block !important;
    height: 15px !important;

    padding-left: 25px !important;
    position: relative !important;
    margin-top: -8px !important;
    left: -11px !important;
    width: 15px !important;
}

#faves.md [id^="FCO-photo-"] {
    display: inline-block !important;
    margin-top: -12px !important;
top : 14px !important;
    position: relative !important;
    left:-18px !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#faves.md [id^="FCO-photo-"]:hover {
    opacity:1 !important;
}
#faves.md [id^="FCO-image-count-"] {
    display: inline !important;
    height: 15px !important;
    margin: 0 auto !important;
    padding-left: 25px !important;
    position: relative !important;
    top: 29px !important;
    width: 15px !important;
}

#archives.md [id^="FCO-photo-"] {
    display: inline-block!important;
    top:17px !important;
    position: relative !important;
    margin-left:-50px !important;
    left:29px !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#archives.md [id^="FCO-photo-"]:hover {
    opacity:1 !important;
}
#archives.md [id^="FCO-image-count-"]{
    display: inline !important;
    height: 15px !important;
    padding-left: 25px !important;
    position: relative !important;
    top: 12px !important;
    margin-left:-50px !important;
    left:42px !important;
    z-index:100 !important;
}

/* SETs "DETAIL VIEW" (md) - MEDIUM THUMBNAIL - COMMENT ICON - === */
#ViewSet.set-view-list .md [id^="FCO-image-"] {
    position:relative !important;
    margin-top:25px !important;
}
/* SETs "DETAIL View" (md) - COMMENT ICON - === */
#ViewSet.set-view-list .md .thumb [id^="FCO-photo-"] {
    margin-top: -50px !important;
    top: -27px !important;
    margin-left:-33px !important;
    position:relative !important;
    z-index:100 !important;
}
/* SETs "DETAIL View" (md) - COUNT - === */
#ViewSet.set-view-list .md [id^="FCO-image-count-"]{
    top: 33px !important;
    margin-left:4px !important;
    position:relative !important;
}



/* ==== LARGE (lg) ==== */

/* POOL /FAV /ARCHIVE - LARGE (lg) - FLUID - COMMENT ICON / COUNT - - === */
#pool-photos.lg [id^="FCO-photo-"] {
    display: inline-block !important;
    border-radius: 5px 5px 5px 5px !important;
    height: 15px !important;
    left: -80px !important;
    padding-left: 25px !important;
    position: relative !important;
    top: 20px !important;
    width: 0 !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#pool-photos.lg [id^="FCO-photo-"]:hover {
    opacity:1 !important;
}
#pool-photos.lg [id^="FCO-image-count-"]{
    display: inline-block !important;
    height: 15px !important;
    padding-left: 25px !important;
    position: relative !important;
    top: 28px !important;
    width: 15px !important;
    margin:auto !important;
    z-index:100 !important;
    left:-12px !important;
}

#faves.lg [id^="FCO-photo-"] {
    border-radius: 5px 5px 5px 5px !important;
    height: 15px !important;
    left: -600px !important;
    padding-left: 25px !important;
    position: relative !important;
    top: 22px !important;
    width: 0 !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#faves.lg [id^="FCO-photo-"]:hover {
    opacity:1 !important;
}

#archives.lg [id^="FCO-photo-"] {
    display: inline-block !important;
    border-radius: 5px 5px 5px 5px !important;
    height: 15px !important;
    left: -80px !important;
    padding-left: 25px !important;
    position: relative !important;
    top: -12px !important;
    width: 0 !important;
    z-index:100 !important;
    opacity:0.5 !important;
}
#archives.lg [id^="FCO-photo-"]:hover {
    opacity:1 !important;
}
#archives.lg [id^="FCO-image-count-"]{
    display: inline-block !important;
    height: 15px !important;
    padding-left: 25px !important;
    position: relative !important;
    top: 28px !important;
    width: 15px !important;
    margin:auto !important;
    z-index:100 !important;
    left:-12px !important;
}


/* ==== JUSTIFIED (ju) ==== */

/* POOL ok/ FAV ok/NEW CONTACT Upload - JUSTIFIED (ju) - FLUID - COMMENT ICON / COUNT - === */
#pool-photos.ju [id^="FCO-photo-"] {
    border-radius: 5px 5px 5px 5px !important;
    position:absolute !important;
    display:block !important;
    margin-top:3px !important;
    margin-left:85% !important;
    z-index:500 !important;
    opacity:0.5 !important;
}
#pool-photos.ju [id^="FCO-photo-"]:hover {
    border-radius: 5px 5px 5px 5px !important;
    opacity:1 !important;
}
#pool-photos.ju [id^="FCO-image-count-"] {
    position:relative !important;
    top:-3px !important;
    left:-12px !important;
    display:inline !important;
}

#faves.ju [id^="FCO-photo-"] {
    border-radius: 5px 5px 5px 5px !important;
    position:absolute !important;
    top:100% !important;
    margin-left:-12% !important;
    z-index:500 !important;
    opacity:0.5 !important;
}
#faves.ju [id^="FCO-photo-"]:hover  {
    opacity:1 !important;
}
#faves.ju [id^="FCO-image-count-"] {
    position:absolute !important;
    top:9px !important;
    margin-left:-7px !important;
}


/* ==== DIVERS ==== */



/* ==== CONTACT ==== */

/* - YOUR CONTACTS RECENT - Thumbnail - COMMENT ICON / COUNT - === */
.HoldPhotos p.RecentPhotos img[id^="FCO-image-"]:not([id^="FCO-image-count-"]){
    top:4px !important;
    position:relative !important;
    left:-22px !important;
    z-index:100 !important;
}
.HoldPhotos p.RecentPhotos span[id^="FCO-image-count-"]{
    top:0px !important;
    position:relative !important;
    left:-32px !important;
}





/* RECENT ACTIVITY STATS SIDE BAR - COMMENT ICON - === */
#toto-main.recent-activity .tt-stats-thumb [id^="FCO-photo-"]{
    position: relative !important;
    margin-top: -1px !important;
}

#toto-main.recent-activity span[id^="FCO-image-count-"]{
    position: relative !important;
    top: -3px !important;
margin-left: 4px !important;
}


/* ====== END ====== */

}
