css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {
   /* From Gmail Fixed-width Font */
   /* [for text message] */
   div.ii, textarea.Ak,
   div.yq, div.yy span {
               font-family: 'courier new', monospace !important;
               font-size: 10pt !important;
   }
   /* [for HTML message] */
   div.ii p, div.ii li, div.ii table, div.ii h3, div.ii h4,
   div.yq p, div.yq li, div.yq table, div.yq h3, div.yq h4 {
       font-family: sans-serif;
       font-size: 100%;
   }
}
