css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

.post_content blockquote {
	  margin-top: 5px !important;
      margin-bottom: 5px !important;
}
  
.post_tags { 
      margin-top: 10px !important;
}
  
.post_content p, .post_body p {
	  margin-bottom: 10px !important;  
}

.post_tag:hover {
      color: #5B5B5B !important;
}
    
.post_info_link:hover, .note_link_current:hover {
      color: #666 !important;    
      text-decoration: none !important;
} 
        
.post_source_link:hover {
      color: #5B5B5B !important;    
      text-decoration: none !important;
}
    
.post_tag, .post_info_link, .note_link_current {
      -webkit-transition: color 0.6s;
      transition: color 0.6s;
      -moz-transition: color 0.6s;
}
  
.post_content a, .post_body a {
	  color: #3C5E87 !important;    
      text-decoration: none !important;
      border-bottom: none;
      -webkit-transition: color 0.6s;
      transition: color 0.6s;
      -moz-transition: color 0.6s;
 	  font-family: helvetica;
      background-image: url('http:') !important;
      margin-bottom: 5px !important;
      
}
    
.post_body a:hover, .post_content a:hover{
	  color: #708DA0 !important;      
}
    
.link_text a {
      color: #ffffff !important;    
      text-decoration: none !important;
}
    
.link_text a:hover {
      color: #daffda !important;    
}    

}
