@-moz-document domain("infosperber.ch") {

/* Abstand von oberem Seitenrand verkleinern */
div#logo
	{
	padding-top: 0 !important;
	}

/* Inhalt zentrieren */
div#wrapper
	{
	width: 815px !important;
	margin: auto !important;
	}

/* Weisser Hintergrund */
div#contentWrap
	{
	background: white;
	padding: 0 10px !important;
	margin-left: -10px !important;
	border-radius: 10px;
	border: 1px solid #737372;
	}

/* Titel: Grössere Schrift */
div.newsItem>h1
	{
	font-size: 24px !important;
	line-height: 28px !important;
	}
/* Artikel: Grössere Schrift und Blocksatz */
div.newsItem p
	{
	font-size: 14px !important;
	line-height: 18px !important;
	text-align: justify;
	}

/* Rechte Sidebar klarer abgrenzen */
div#additionalContent>div.preview
	{
	border: 1px solid #CCCCCC;
	width: 288px !important;
	padding:5px !important;
	}

}
