css:
@-moz-document url("chrome://browser/content/tabview.html")
{
/*Hide these:*/
.appTabTrayContainer {display:none !important}

/*Style Tabs*/
.tab {
 background-color: rgba(255,255,255,0.4) !important;
 border-radius: 0 !important;
 padding:3px 5px 5px 3px !important;
}
.tab-title, input.name {
 color: #FFF !important;
 font-weight: bold !important;
 text-shadow: -1px 1px 1px black !important;
}

/*Show Close Tab Button only on hover*/
.tab .close{display:none;}
.tab:hover .close{display:inline;}

/*Style Tab Groups*/
.groupItem {
 background-color: rgba(255,255,255,0.3) !important;
 border-radius: 0 !important;
 box-shadow: rgba(0,0,0,0.8) 2px 2px 5px !important;
}
.groupItem.activeGroupItem {
 box-shadow: 
  0  1px  0  #FFFFFF inset,
  0 -1px 1px #FFFFFF inset,
  -1px 0 1px #FFFFFF inset,
  1px  0 1px #FFFFFF inset,
  0 1px 3px  rgba(4, 38, 60, 0.6) !important;
}

/*Style Tab Favicon*/
.favicon{background-color: rgba(255,255,255,0.8)!important;
 border: 0 !important;
 border-radius: 0 !important;
}
.favicon img{
 padding-left:1px !important
}
}
