css:
@-moz-document domain("formspring.me") {


a.smile span.a_smile {  
    display: none;  
}  
a.smile:before {  
    background: url("http://i.imgur.com/KVmPI.png") no-repeat scroll 0 1px transparent;  
    content: "CHEEEEEEEEEEEEN";  
    display: block;  
    float: left;  
    margin-right: 5px;  
    padding-left: 35px;  
    padding-top: 5px;  
    height: 20px; 
}

a.smiled span.a_smile {  
    display: none;  
}  

a.smiled:before {  
    background: url("http://i.imgur.com/KVmPI.png") no-repeat scroll 0 1px transparent;  
    content: "CHEEEEEEEEEEEEN'd";  
    display: block;  
    float: left;  
    margin-right: 5px;  
    padding-left: 35px;  
    padding-top: 5px;  
    height: 20px; 
}

#header #sub .smileshuffle{background:url("http://i.imgur.com/JUTVZ.png") 0 0 no-repeat;display:inline-block;height:36px;margin-right:10px;overflow:hidden;text-indent:-999px;width:34px;}
}
