css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("na.leagueoflegends.com") {
    body, #content_area, #layout_2_content, #container_top,
    #container_bottom, #page_header, #hammer_splash,
    #hammer_objective, #objective_piece_one, #objective_piece_two, 
    #objective_piece_three, #guidelines_header, 
    .judge_header, #judger_details, .judger_holder, .judger_holder_top, .judger_holder_bottom,
    #judger_report, #judger_report_footer, .judger_killdeath, .judge_footer, #csb_captcha_image { 
        background: none !important;
    }
    #header, #f2phold, #lol-pvpnet-bar, #footer, .judge_body, #judger_reportlist, #judger_skip, #judger_finished {
        display: none
    }
    #judger_punish, #judger_skip, #judger_pardon, #judger_next, #judger_previous {
        text-align: center;
        background: #000 !important;
        font-size: 12pt;
        height: 20pt !important;
        color: #fff;
    }
    #judger_title { background: #000 !important; }
    #judger_punish:after { 
        content: "Punish";
    }
    #judger_skip:after { 
        content: "Skip";
    }
    #judger_pardon:after { 
        content: "Pardon";
    }
    #judger_next {
        content: "Next";
    }
    #judger_previous {
        content: "Previous";
    }
    #judger_game {
        color: #000 !important;
    }
    .enemy, .allied {
        color: #000 !important;
        background-color: #fff !important;
    }
    .judger_header {
        color: #000 !important;
    }
    .judger_statistics_container { background-color: #fff !important }
    #judger_right { 
        float: none !important; width: 100% !important; text-align: center !important;
        margin-left: auto !important; margin-right: auto !important; display: block !important;
    }
    #judger_left { float: none !important; }
}
