css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://apinfo.com/naveg.htm"), url("http://www.apinfo.com/naveg.htm"), url("http://apinfo2.com/naveg.htm"), url("http://www.apinfo2.com/naveg.htm") {
 a img, td img, td object { display: none!important; }
}

@-moz-document domain("apinfo.com"), domain("apinfo2.com")  {
 center a img, center object, font a img, font object, hr, td p font font img { display: none!important; }
 table, td, span { background-image: none!important; background-color: #FFF!important; }
 b font, body div center table tbody tr td p font, body table tbody tr td h3 a, body table tbody tr td h3 { font-size: 14px!important; }
 center { text-align: left!important; }
 center p b font, a { color: #000!important; }
}
