@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* ==UserStyle==
// @name         BackButton with Text selective on right
// @author       Roblesolido
// @compatible   Firefox 3
// @description	 Buttons with selective text on right.
// ==/UserStyle==*/

toolbar[mode="icons"][iconsize="large"] #back-button
{
-moz-box-orient: horizontal !important;
}
toolbar[mode="icons"][iconsize="large"] #back-button .toolbarbutton-text
{
display: -moz-box !important;
margin-left: 4px !important;
margin-right: 4px !important;
}
