css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.google.co.jp") {
    .section-name {
        font-size: 100%;
    }
    .basic-title  {
        padding: 0.4em !important;
    }
    .basic-title  h2 {
        font-size: 120% !important;
    }
    .story, .esc-lead-snippet-wrapper{ 
        border-top: 1px dotted #ccc;
        margin: 0.15em 0em;
    }
    .titletext { 
        font-size: 110%; 
    }
    .esc-lead-snippet-wrapper { 
        font-size: 110%;
        line-height: 1.3 !important;
    }
}
