css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.engadget.com") {

#outerslice, .topleader, .bottomleader, #grid, #footer, #subcontent, #header, .postmeta, .byline, #recentheadlines, #recentheadlines + ul, .inpostad
#innercontent > div:first-child > .byline + div > div + span
  { display: none !important; }

#container, #wrapper, #wrapper2, #content, #innercontent, #innercontent > div
{ 
   width: 100% !important;
   max-width: none !important;
   margin: 0 !important;
   padding: 0 !important;
}

#innercontent > div
{
   padding: 2% !important;
   width: 96% !important; 
   text-align: justify !important;
   font-size: 12pt !important;
   font-family: arial, sans-serif !important;
   max-width: none !important;
}

}
