css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("help.adobe.com") {
  body {
    background: #222 !important;
    color: #999 !important;
  }
  strong {
    color: #999 !important;
  }
  a {
    color: #777!important;
  }
  .summaryTableDescription {
    color: #555!important;
  }
  table, .titleTableTitleION, .titleTableSearchION, .titleTableRow2, table tr[bgcolor='#FFFFFF'], #ionCount .ionComAddForm {
    background: transparent !important;
    color: #999 !important;
    position: relative;
  }
  div#content_wrapper div table tr th, div.separator, #group_toc p, #ionMessageContainer .ionComMessage, .summaryTable th {
    background: #444 !important;
    color: #ccc !important;
  }
  dfn.term, pre, .titleTableRow3, div.splitter, .detailSectionHeader, table tr[bgcolor='#F2F2F2'] {
    background: #1a1a1a!important;
    color: #999 !important;
  }
  table tr[bgcolor='#FFFFFF'] {
    background: #111 !important;
    color: #999 !important;
  }
  .detailHeaderRule, .detailHeaderType, .detailHeaderName {
    background: transparent !important;
    border-bottom: 1px solid #111 !important;
    padding-bottom: 3px !important;
  }
  hr {
    background: transparent !important;
    border: 1px solid #111 !important;
  }
  .titleTableLogo {
    display: none!important;
  }
}
