css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.webcg.net") {

/* Non-display part */
#headerRight,
/*#main_area > .site_w,*/
#main_area > .site_w > div > div > table > tbody > tr > td > a,
#main_area > div > table > tbody > tr > td.bg_blue,
#footer,
#main_area > br,
#main_area > div > table > tbody > tr > td > .line_blue_bottom > table,
#main_area > div > table > tbody > tr > td > .line_blue_bottom > div
{ display: none !important; }

/* Display part */
body,
#main_area,
.bg_white
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

#main_area > div > table
{
    width: 100% !important;
}

}
