css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zoom-online.dk"), domain("mobil.nu") {

.ad,
.rss,
.adcolumn,
#topHeader,
#farRight,
#crossSiteRSSElement,
#navigationSubscriptionAd,
.pricerunnerAdContainer,
*[src*="mediaprovider"],
*[src*="berlingske"],
*[src*="adform.net"],
*[src*="adviva.net"],
#articleAd,
#spacer,
.adLabel,
.pricerunner,
.articleBanner,
#wide_afc_bottom,
.topHeader
{display:none!important}

#site {
width: 960px !important;
margin: auto !important; }

/*
.comoffTop {
margin-bottom: 5px !important;
background: #00314D !important; }
*/

#site #page .main .container .article {
padding-bottom: 38px !important; }

/* Facebook + Share service */
.FB_SERVER_IFRAME,
.fb_iframe_widget,
.FB_fan,
.likeButton,
.utilBoxShare,
*[src*="facebook.com/plugins/likebox"]
{display:none!important}

/* MediaMac */
*[style*="1215px"] {width:960px !important}

/* kommentarer wide */
#site #page .main .container .discussion {
width: 630px !important }

#site #page .webtvcontainer .discussion .writeComment, #site #page .webtvcontainer .discussion .commentsList {
width: 640px !important }

}
